var db = require( "skyerp/dao/db.js" );
var server = require( "skyerp/server.js" );

var App = {
    run: function ( ) {
        db.connect( );
        db.setup( );
        server.listen( );
    	//server.listen(9000, "192.168.108.97");

    }
};

process.on( 'SIGINT', function ( ) {
    db.close( );
    console.log( "\n SIGINT (Crtl-C)" );
    // Sortie
    process.exit( );
} );

App.run( );